(function ($) {
	$(document).ready(function () {
		"use strict";
		
		if (!Modernizr.svg) {
			$('img[src*="svg"]').attr('src', function() {
				return $(this).attr('src').replace('.svg', '.png');
			});
		}
		
		var scroll_easing = 'easeInOutQuint', scroll_speed = 1200, scroll_offset = -48;

		if (location.hash) {
			window.scrollTo(0, 0);
			$.scrollTo( $(location.hash), scroll_speed, { 'axis':'y', offset: scroll_offset, easing: scroll_easing });
		}

		$('a').click(function() {
			var target = this.hash;
			//console.log(target)
			if (target.length != 0) {
				$.scrollTo( $(target), scroll_speed, { 'axis':'y', offset: scroll_offset, easing: scroll_easing });
				history.pushState('', '', target);
				return false;
			}
		});
		
		var $nav = $('#access .menu');

		function navigation_main_open() {
			$nav.animate({
				right: '0px'
			}, {
				duration: '300',
				easing: 'easeInOutQuint'
			});
		}
		
		function navigation_main_close() {
			$nav.animate({
				right: '-240px'
			}, 200);
		}
		
		$('.trigger-navigation-main').click(function() {
			navigation_main_open();
			return false;
		});
		
		$('#menu-item-637 a').click(function() {
			navigation_main_close();
			return false;
		});
		
		// MAILCHIMP FORM
		$('.mc_embed_signup label').inFieldLabels();
		
		// CONTRIBUTE FORM
		$('#customButton').click(function() {
			var contribute_amount = $('#contribute-amount').val();
	    // Open Checkout with further options
	    handler.open({
	      name: 'Tidepool',
	      description: 'Donate to Tidepool',
	      amount: contribute_amount
	    });
	  });
		
		// OPEN EXTERNAL SITES IN NEW WINDOW
		$("a[href*='http://']:not([href*='"+location.hostname+"'])").attr("target","_blank");
		
		// WIDOWS
		$('#slideshow li h3, #slideshow li p, #page-content-header h3').widowFix();
		
	});
})(jQuery);